import { useState } from 'react';
import Popup from './components/Popup';

function App() {
  const todoItems = [
    {
      id: 2,
      title: 'Buy avocados from Calbee',
      description: 'I need to buy some more avocados',
      completed: true,
    },
    {
      id: 3,
      title: 'Need to learn Django REST Framework',
      description: 'Yes, please more knowledge!',
      completed: false,
    },
    {
      id: 4,
      title: 'Study some more Data Analysis and SQL',
      description: 'I need to study more PostgreSQL',
      completed: false,
    },
    {
      id: 5,
      title: 'Study MySQL',
      description: 'Learn the basics of SQL coding of database',
      completed: true,
    },
    {
      id: 6,
      title: 'Django REST Framework is so awesome!!!!',
      description: "WHAT THE HELL!! I'm freaking out!!! Django is awesome!!",
      completed: true,
    },
  ];

  // States
  const [viewCompleted, setViewCompleted] = useState(false);
  const [todoList, setTodoList] = useState(todoItems);
  const [modal, setModal] = useState(false);
  const [activeItem, setActiveItem] = useState({
    title: '',
    description: '',
    completed: false,
  });

  // Functions

  const toggle = () => {
    setModal(!modal);
  };

  const handleSubmit = (item) => {
    toggle();
    alert('save' + JSON.stringify(item));
  };

  const handleDelete = (item) => {
    alert('delete' + JSON.stringify(item));
  };

  const createItem = () => {
    const item = { title: '', description: '', completed: false };

    setActiveItem(item);
    setModal(!modal);
  };

  const editItem = (item) => {
    setActiveItem(item);
    setModal(!modal);
  };

  const displayCompleted = (status) => {
    if (status) {
      return setViewCompleted(true);
    }

    return setViewCompleted(false);
  };

  const renderTabList = () => (
    <div className='nav nav-tabs'>
      <span
        className={viewCompleted ? 'nav-link active' : 'nav-link'}
        onClick={() => displayCompleted(true)}
      >
        Complete
      </span>
      <span
        className={viewCompleted ? 'nav-link' : 'nav-link active'}
        onClick={() => displayCompleted(false)}
      >
        Incomplete
      </span>
    </div>
  );

  const renderItems = () => {
    const newItems = todoList.filter((item) => item.completed == viewCompleted);

    return newItems.map((item) => (
      <li
        key={item.id}
        className='list-group-item d-flex justify-content-between align-items-center'
      >
        <span
          className={`todo-title mr-2 ${viewCompleted ? 'completed-todo' : ''}`}
          title={item.description}
        >
          {item.title}
        </span>
        <span>
          <button className='btn btn-secondary mr-2'>Edit</button>
          <button className='btn btn-danger'>Delete</button>
        </span>
      </li>
    ));
  };

  return (
    <div className='App'>
      <main className='container'>
        <h1 className='text-white text-uppercase text-center my-4'>Todo app</h1>
        <div className='row'>
          <div className='col-md-6 col-sm-10 mx-auto p-0'>
            <div className='card p-3'>
              <div className='mb-4'>
                <button className='btn btn-primary'>Add task</button>
              </div>
              {renderTabList()}
              <ul className='list-group list-group-flush border-top-0'>
                {renderItems()}
              </ul>
            </div>
          </div>
        </div>
        {modal ? (
          <Popup
            activeItem={activeItem}
            setActiveItem={setActiveItem}
            toggle={toggle}
            onSave={handleSubmit}
          />
        ) : null}
      </main>
    </div>
  );
}

export default App;
